class Field {
    constructor(portrait, paysage) {
        this.values = {
            'portrait': portrait,
            'paysage': paysage
        }
    }
    getValues() {
        return this.values;
    }
}

const css = {
    '--navigation-bar-height': new Field('140px', '90px'),
    '--navbar-item-width': new Field('100px', '150px'),
    '--navbar-item-text-font-size': new Field('15px', '25px'),
    '--title-font-size': new Field('8px', '25px'),
    '--title-icon-size': new Field('15px', '42px')
}

const updateCSS = (typeScreen) => {
    Object.entries(css).forEach(([ variable, field ]) => {
        document.documentElement.style.setProperty(variable, field.getValues()[typeScreen]);
    });
}

export default updateCSS;
