import { useRef } from "react";
import { useState } from "react";

import LeftToRightComponents from "../utils/components/LeftToRightComponents";
import { IoChevronBack, IoChevronForward } from "react-icons/io5";

function Home() {
	const [selected, setSelected] = useState(null);

	const childRef = useRef();

	return (
		<>
			<div style={{ width: "100%", display: "flex", alignItems: "stretch", flexDirection: "row" }}>
				<span
					onClick={() => childRef.current && !childRef.current.isFirst() && childRef.current.previous()}
					className={`left-to-right-side-button${
						childRef.current && childRef.current.isFirst() ? " disabled" : ""
					}`}>
					<IoChevronBack />
				</span>
				<LeftToRightComponents
					height='50vh'
					ref={childRef}
					border='1px solid red'
					flex={1}
					durationInMs={200}
					updateSelectedOnParent={(res) => setSelected(res.join("/"))}>
					<>Page 1 aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa</>
					<>Page 2</>
					<>Page 3</>
				</LeftToRightComponents>
				<span
					onClick={() => childRef.current && !childRef.current.isLast() && childRef.current.next()}
					className={`left-to-right-side-button${
						childRef.current && childRef.current.isLast() ? " disabled" : ""
					}`}>
					<IoChevronForward style={{ margin: "0 auto" }} />
				</span>
			</div>
			Page {selected}
		</>
	);
}

export default Home;
