import { useCallback } from "react";
import { useForm } from "react-hook-form";
import Modal from "react-modal";
import useWindowDimensions from "../../../utils/hooks/useWindowDimensions";

const customStyles = {
	overlay: { // PAGE BEHIND THE MODAL
		backgroundColor: "rgba(255, 255, 255, 0.75)",
	},
	content: {
		backgroundColor: "var(--dark-gray)",
		border: "3px solid var(--orange-2)",
		// CENTERED CONTENT
		top: "50%",
		left: "50%",
		right: "auto",
		bottom: "auto",
		marginRight: "-50%",
		transform: "translate(-50%, -50%)",
	},
};

Modal.setAppElement("#root");

export default function MeContacter({ isOpen, setIsOpen }) {
	const {
		register,
		handleSubmit,
		formState: { errors },
		reset,
	} = useForm();

	const {width: winWidth} = useWindowDimensions();

	const submit = useCallback(
		(data) => {
			console.log(data);
			reset();
		},
		[reset]
	);

	return (
		<Modal isOpen={isOpen} onRequestClose={() => setIsOpen((_) => false)} contentLabel='test' style={customStyles}>
			{/* TODO: X button */}
			<form onSubmit={handleSubmit(submit)} className='formulaire'>
				<h3>Me contacter</h3>

				<div className='corps'>
					<label>Nom</label>
					<input
						{...register("name", { required: true })}
						placeholder='Nom'
						className={errors.name && "invalid"}
					/>
					<span className='error'>{errors.name && errors.name.message}</span>

					<label>Mail</label>
					<input
						{...register("email", {
							pattern: /^[A-Za-z0-9]+@[a-zA-Z0-9.]+\.[a-zA-Z]{2,4}$/i,
							required: "L'e-mail donnÃ©e n'est pas au bon format",
						})}
						placeholder='E-mail'
						className={errors.email && "invalid"}
					/>
					<span className='error'>{errors.email && errors.email.message}</span>

					<label>Message</label>
					<textarea
						{...register("message", { required: true })}
						placeholder='Message'
						className={errors.message && "invalid"}
						rows={5}
						style={{width: winWidth * 0.2}}
					/>
					<span className='error'>{errors.message && errors.message.message}</span>
				</div>

				<input type='submit' value={"Envoyer"} className='submit' />
			</form>
		</Modal>
	);
}
