import { useEffect, useState } from "react"
import { useNavigate } from "react-router";
import { motion } from "framer-motion";

function NavigationBar(props) {

    const navigate = useNavigate();
    
    const pagesList = props.pagesList;
    const [ currentPageName, setCurrentPageName ] = useState(props.currentPageName);

    useEffect(() => {
        setCurrentPageName(props.currentPageName)
    }, [props.currentPageName])

    return (
        <motion.div
            exit={{y: '-90%'}}
            animate={{y: 0}}
            initial={{y: '-90%'}}
            transition={{duration: 0.5}}
        >
            <ul className="navigation-bar">
                {   
                    pagesList.slice().reverse().filter(page => !page.hidden).map(({name, path}) => {
                        return (
                            <li key={name} className={name === currentPageName ? "active" : ""} onClick={() => navigate(path)} >
                                <span>{name}</span>
                            </li>
                        )
                    })
                }
            </ul>
        </motion.div>
    )

}

export default NavigationBar;
