import { useState, useEffect } from "react"
import Loading from "../utils/Loading"
import axios from "axios"

const num = "selecteur_machine";
const type = "nom_type";
const statut = "status";
const tempsRestant = "time_before_off";

function Machines() {

    const [ machinesData, setMachinesData ] = useState({
        isLoading: true,
        data: {}
    })

    useEffect(() => {
        axios.get('https://coman-machines-api.herokuapp.com')
        .then(reponse => {
            setMachinesData({
                isLoading: false,
                data: reponse.data.machine_info_status.machine_list
            })
        })
    }, [])
    
    let timeFormat = (secondes) => {
        const minutes = Math.floor(secondes / 60);
        const newSecondes = secondes - minutes * 60;
        const minutesString = minutes < 10 ? `0${minutes}` : `${minutes}`;
        const newSecondesString = newSecondes < 10 ? `0${newSecondes}` : `${newSecondes}`;
        return minutesString + ":" + newSecondesString;
    }

    return machinesData.isLoading ? <Loading /> : (
        <table>
            <thead>
                <tr>
                    <td>Numéro</td>
                    <td>Type</td>
                    <td>Statut</td>
                    <td>Temps Restant</td>
                </tr>
            </thead>
            <tbody>
                {machinesData.data.map(machine => {
                    return (
                        <tr key={machine[num]}>
                            <td>{machine[num]}</td>
                            <td>{machine[type].trim()}</td>
                            <td>{machine[statut] === 1 ? "Libre" : "Occupé"}</td>
                            <td>{machine[statut] === 1 ? "-" : timeFormat(machine[tempsRestant])}</td>
                        </tr>
                    )
                })}
            </tbody>
        </table>
    );
}

export default Machines;
