import React from "react";
import { useMemo } from "react";
import { useCallback, useState } from "react";

import Button from "./Button";
import LeftToRight from "../../pages/LeftToRight";

function LeftToRightComponents({children}) {

    const [ selected, setSelected ] = useState(0);

    const max = useMemo(() => React.Children.count(children) - 1, [children]);

    const next = useCallback(() => {
        setSelected(selected => selected < max ? selected + 1 : selected);
    }, [max])

    const previous = useCallback(() => {
        setSelected(selected => selected > 0 ? selected - 1 : selected);
    }, [])

    const selector = useCallback(() => {
        return (
            <>
            <Button onClick={previous}>Précédent</Button>
            <Button onClick={next}>Suivant</Button>
            </>
        )
    }, [next, previous])
    
    return children && (
        <>
        {selector()}
        <LeftToRight>
            {React.Children.map(children, (child, i) => selected === i && child)}
        </LeftToRight>
        </>
    );

}

export default LeftToRightComponents;
