class Field {
    constructor(verySmall, mobile, tablet, laptop, bigScreen) {
        this.values = {
            'verySmall': verySmall,
            'mobile': mobile,
            'tablet': tablet,
            'laptop': laptop,
            'bigScreen': bigScreen
        }
    }
    getValues() {
        return this.values;
    }
}

const css = {
    '--navigation-bar-height': new Field('60px', '50px', '70px', '85px', '90px'),
    '--navbar-item-width': new Field('70px', '100px', '130px', '180px', '200px'),
    '--navbar-item-text-font-size': new Field('8px', '12px', '15px', '20px', '25px'),
    '--title-font-size': new Field('8px', '12px', '18px', '20px', '25px'),
    '--title-icon-size': new Field('15px', '20px', '30px', '35px', '42px')
}

const updateCSS = (typeScreen) => {
    Object.entries(css).forEach(([ variable, field ]) => {
        document.documentElement.style.setProperty(variable, field.getValues()[typeScreen]);
    });
}

export default updateCSS;
