import { changeLanguage } from 'i18next';
import Select from 'react-select';
import info from '../../languages/info';
import database from '../../utils/database';

const options = info.map(lang => {
    return { value: lang.language, label: lang.label }
}).concat([
    { value: 1, label: 1 },
    { value: 2, label: 2 },
    { value: 3, label: 3 }
]);

const colourStyles = {
    control: styles => ({ ...styles, backgroundColor: 'white' }),
    option: (styles, { data, isDisabled, isFocused, isSelected }) => {
        return {
        ...styles,
        border: '1px solid gray',
        borderRadius: '10px',
        backgroundColor: isFocused ? 'var(--green)' : 'white',
        color: isFocused ? 'var(--white)' : 'black',
        margin: '2px'
        };
    },
    menu: base => {
        return {
            ...base,
            backgroundColor: 'var(--white)',
            padding: '8px',
            border: '1px solid var(--green)'
        }
    }
};

const LanguageSelectorField = ({ userData, userID }) => {

    const handleChange = ({ value }) => {
        changeLanguage(value);
        database.setUserData(userID, 'language', value);
    }

    return (
        <Select
        classNamePrefix="selection-component"
        defaultValue={options.find(lang => lang.value === userData.language)}
        options={options}
        className="selection-component"
        onChange={handleChange}
        styles={colourStyles}
        hideSelectedOptions
        menuIsOpen
        />
    );

};

export default LanguageSelectorField;
