import { useCallback, useEffect, useState } from "react";

function Track({ trackHeight }) {
	const [isScrolling, setIsScrolling] = useState(false);
	const [top, setTop] = useState(0);

	const onMouseMove = useCallback((e) => {
		console.log(e);
	}, []);

	const onMouseUp = useCallback((_) => {
        console.log("UP");
        setIsScrolling(false);
		window.removeEventListener("mousemove", onMouseMove, true);
		window.removeEventListener("mouseup", onMouseUp, true);
	});

	const onMouseDown = useCallback((_) => {
        console.log("DOWN");
		setIsScrolling(true);
		window.addEventListener("mousemove", onMouseMove, true);
		window.removeEventListener("mouseup", onMouseUp, true);
	}, []);

	return (
		<div
			className='track'
			style={{ width: "100%", height: trackHeight && trackHeight !== Infinity ? trackHeight : 0 }}
			onMouseDown={onMouseDown}
		/>
	);
}

function Scrollbar({ totalHeight, cappedHeight }) {
	const [trackHeight, setTrackHeight] = useState(null);

	useEffect(() => {
		setTrackHeight((cappedHeight * cappedHeight) / totalHeight);
	}, [cappedHeight, totalHeight]);

	return (
		<div className='scrollbar'>
			<Track trackHeight={trackHeight} />
		</div>
	);
}

function WithScrollbarComponent({ Child }) {
	const [totalHeight, setTotalHeight] = useState(null);
	const [cappedHeight, setCappedHeight] = useState(null);

	return (
		<div className='scrollable'>
			<Child
				ref={(node) =>
					node &&
					setTimeout(() => {
						console.log(node.clientHeight, node.scrollHeight);
						node.clientHeight && setCappedHeight(node.clientHeight);
						node.scrollHeight && setTotalHeight(node.scrollHeight);
					}, 2)
				}
			/>
			<Scrollbar totalHeight={totalHeight} cappedHeight={cappedHeight} />
		</div>
	);
}

export default function withScrollbar(Child) {
	return () => <WithScrollbarComponent Child={Child} />;
}
