import { Component, useEffect, useState } from "react"
import "./style.css"
import { Link } from "react-router-dom";

function NavigationBar() {
    
    const pagesList = props.pagesList;
    const [ currentPageName, setCurrentPageName ] = useState(props.currentPageName);

    useEffect(() => {
        setCurrentPageName(this.props.currentPageName)
    }, [props.currentPageName])

    return (
        <ul className="navigation-bar">
            {
                pagesList.slice().reverse().map(({name, path}) => {
                    return (
                        <li key={name} className={name === currentPageName ? "active" : ""}>
                            <Link to={path}>
                                {name}
                            </Link>
                        </li>
                    )
                })
            }
        </ul>
    )

}

export default NavigationBar;
