import "./style.css"
import React from "react"
import { useLocation } from "react-router"
import ReactDOM from "react-dom"
import Home from "./pages/Home"
import Other from "./pages/Other"
import NavigationBar from "./NavigationBar"
import { BrowserRouter, Route, Routes, Navigate } from "react-router-dom";
import { AnimatePresence } from "framer-motion"
import PageNotFound from "./pages/PageNotFound"
import { WrapFondu } from "./utils/appTemplates"

function Page() {

  const location = useLocation()

  const pagesList = [
    {
        name: "Home",
        path: "/home",
        app: <Home />
    },
    {
        name: "Other",
        path: "/other",
        app: <Other />
    },
    {
      name: "PageNotFound",
      path: "/pageNotFound",
      app: <PageNotFound />,
      hidden: true
    }
  ]

  let getPagesRoutes = () => {
    return (
      pagesList.map( ({ name, path, app }) => {
        return (
          <Route key={name} path={path} element={
            <div className="page">
              <div className="navbar"><NavigationBar currentPageName={name} pagesList={pagesList} /></div>
              <WrapFondu app={<div className="app-container">{app}</div>} />
            </div>
          } />
        )
      })
    )
  }

  return (
        <AnimatePresence exitBeforeEnter>
          <Routes location={location} key={location.pathname}>

            <Route exact path="/" element={<Navigate to="/home" state/>} />

            {getPagesRoutes()}

            <Route path="*" element={<Navigate to="/pageNotFound" state/>} />

          </Routes>
        </AnimatePresence>
  );
}

const rootElement = document.getElementById("root")
ReactDOM.render(<BrowserRouter><Page /></BrowserRouter>, rootElement)
