import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';

import info from './languages/info';

const res = Object.fromEntries(info.map(lang => { return [ lang.language, { translation: require(lang.path) } ] }));

const resources = {
    fr: {
        translation: translationFR
    },
    en: {
        translation: translationEN
    }
}

i18n
    .use(initReactI18next)
    .init({
        resources: resources,
        lng: 'en',

        interpolation: {
            escapeValue: false,
        }
    });

    export default i18n;
