import { useCallback } from "react";
import { toast } from "react-toastify";

function CopyText({ children, className = "", overrideText = undefined, ...rest }) {

	const notify = () => {
		toast.success('Copié', {
			position: "top-right",
			autoClose: 3000,
			hideProgressBar: false,
			closeOnClick: true,
			pauseOnHover: false,
			draggable: true,
			progress: undefined
		});
	}

	return (
		<span
			{...rest}
			className={`copy-text ${className}`}
			onClick={() => {navigator.clipboard.writeText(overrideText ? overrideText : children).catch(() => {}); notify()}}>
			{children}
		</span>
	);
}

export default CopyText;
