import "./styles/style.css"
import React, { Suspense, useEffect, useState } from "react"
import { useLocation } from "react-router"
import ReactDOM from "react-dom"
import NavigationBar from "./NavigationBar"
import { BrowserRouter, Route, Routes, Navigate } from "react-router-dom"
import { AnimatePresence } from "framer-motion"
import { WrapFondu } from "./utils/appTemplates"
import database from "./utils/database"
import pagesList from "./pages/pagesList"
import { Auth0Provider } from '@auth0/auth0-react'
import { useMediaQuery } from 'react-responsive'
import updateCSS from "./styles/responsive"
import Loading from "./utils/Loading"
import './i18n'
import { changeLanguage } from "i18next"
import useUserData from "./utils/useUserData"

const auth0Domain = process.env.REACT_APP_AUTH0_DOMAIN;
const auth0ClientId = process.env.REACT_APP_AUTH0_CLIENTID;

function Page() {

  const isMobileDevice = useMediaQuery({query: "(min-device-width: 480px)",});
  const isTabletDevice = useMediaQuery({query: "(min-device-width: 768px)",});
  const isLaptop = useMediaQuery({query: "(min-device-width: 1024px)",});
  const isBigScreen = useMediaQuery({query: "(min-device-width: 1200px)",});
  const [ appareil, setAppareil ] = useState('jsp')

  updateCSS('verySmall');
  if(isMobileDevice) {
    updateCSS('mobile');
  }
  if(isTabletDevice) {
    updateCSS('tablet');
  }
  if(isLaptop) {
    updateCSS('laptop');
  }
  if(isBigScreen) {
    updateCSS('bigScreen');
  }

  window.addEventListener("beforeunload", (ev) => 
  {  
      database.disconnect();
  });

  const location = useLocation()

  const { isLoadingAuth, isLoadedUser, isAuthenticated, userData } = useUserData();

  useEffect(() => {
    if (!isLoadingAuth && isAuthenticated && isLoadedUser) {
      changeLanguage(userData['language']);
    }
    setAppareil('verySmall')
    if(isMobileDevice) {
      setAppareil('mobile')
    }
    if(isTabletDevice) {
      setAppareil('tablet')
    }
    if(isLaptop) {
      setAppareil('laptop')
    }
    if(isBigScreen) {
      setAppareil('bigScreen')
    }
  }, [isLoadingAuth, isLoadedUser, isAuthenticated, userData])

  let getPagesRoutes = () => {
    return (
      pagesList.map( ({ name, path, app }) => {
        return (
          <Route key={name} path={path} element={
            <div className="page">
              <NavigationBar currentPageName={name} pagesList={pagesList} />
              <WrapFondu app={<div className="app-container">{appareil}{app}</div>} />
            </div>
          } />
        )
      })
    )
  }

  return (
        <AnimatePresence exitBeforeEnter>
          <Routes location={location} key={location.pathname}>

            <Route exact path="/" element={<Navigate to="/home" state/>} />

            {getPagesRoutes()}

            <Route path="*" element={<Navigate to="/pageNotFound" state/>} />

          </Routes>
        </AnimatePresence>
  );
}

const rootElement = document.getElementById("root")
ReactDOM.render(

  <BrowserRouter>
    <Auth0Provider
      domain={auth0Domain}
      clientId={auth0ClientId}
      redirectUri={window.location.origin + "/home"}
    >
      <Suspense fallback={<Loading />}>
        <Page />
      </Suspense>
    </Auth0Provider>
  </BrowserRouter>
  , rootElement)
