import { useCallback } from "react";
import { toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css'

function CopyText({ children, className = "", overrideText = undefined, copiedText, ...rest }) {

	const notify = useCallback(() => {
		toast.success(`${copiedText}`, {
			position: "top-right",
			autoClose: 3000,
			hideProgressBar: false,
			closeOnClick: true,
			pauseOnHover: false,
			draggable: true,
			progress: undefined,
			theme: 'dark'
		});
	}, [])

	return (
		<span
			{...rest}
			className={`copy-text ${className}`}
			onClick={() => {navigator.clipboard.writeText(overrideText ? overrideText : children).catch(() => {}); notify()}}>
			{children}
		</span>
	);
}

export default CopyText;
