const css = {
    'verySmall' : {
        '--navigation-bar-height': '45px',
        '--navbar-item-width': '70px',
        '--navbar-item-text-font-size': '8px',
        '--title-font-size': '8px',
        '--title-icon-size': '15px'
    },
    'mobile': {
        '--navigation-bar-height': '50px',
        '--navbar-item-width': '100px',
        '--navbar-item-text-font-size': '12px',
        '--title-font-size': '12px',
        '--title-icon-size': '20px'
    },
    'tablet': {
        '--navigation-bar-height': '70px',
        '--navbar-item-width': '130px',
        '--navbar-item-text-font-size': '15px',
        '--title-font-size': '18px',
        '--title-icon-size': '30px'
    },
    'laptop': {
        '--navigation-bar-height': '85px',
        '--navbar-item-width': '180px',
        '--navbar-item-text-font-size': '20px',
        '--title-font-size': '20px',
        '--title-icon-size': '35px'
    },
    'bigScreen' : {
        '--navigation-bar-height': '95px',
        '--navbar-item-width': '200px',
        '--navbar-item-text-font-size': '25px',
        '--title-font-size': '25px',
        '--title-icon-size': '42px'
    }
}

const updateCSS = (typeScreen) => {
    Object.entries(css[typeScreen]).forEach(([ variable, value ]) => {
        document.documentElement.style.setProperty(variable, value);
    });
}

export default updateCSS;
