import { useState } from "react"
import { motion } from "framer-motion"

function Home() {

    const [ number, setNumber ] = useState(1);

    let onClick = (_) => {
        setNumber(number + 1);
    }

    return (
        <motion.div
            exit={{opacity: 0}}
            animate={{opacity: 1}}
            initial={{opacity: 0}}
            transition={{duration: '2s'}}
        >
            <button onClick={onClick}>
                {number}
            </button>
        </motion.div>
    )

}

export default Home;
