import { useEffect, useState } from "react";

function Scrollbar({ totalHeight, cappedHeight }) {
	const [trackHeight, setTrackHeight] = useState(null);

	useEffect(() => {
		setTrackHeight((cappedHeight * cappedHeight) / totalHeight);
	}, [cappedHeight, totalHeight]);

	return (
		<div className='scrollbar'>
			<div className='track' style={{width: '100%', height: trackHeight}}>

            </div>
		</div>
	);
}

function WithScrollbarComponent({ Child }) {
	const [totalHeight, setTotalHeight] = useState(null);
	const [cappedHeight, setCappedHeight] = useState(null);

	useEffect(() => console.log(totalHeight, cappedHeight), [totalHeight, cappedHeight]);

	return (
		<div className='scrollable'>
			<Child
				ref={(node) =>
					node &&
					setTimeout(() => {
						setCappedHeight(node.clientHeight);
						setTotalHeight(node.scrollHeight);
					}, 2)
				}
			/>
			<Scrollbar totalHeight={totalHeight} cappedHeight={cappedHeight} />
		</div>
	);
}

export default function withScrollbar(Child) {
	return () => <WithScrollbarComponent Child={Child} />;
}
