import Select from 'react-select';

const colourStyles = {
    control: styles => ({ ...styles, fontFamily: 'var(--coman-font)', textAlign: 'center' }),
    option: (styles, { data, isDisabled, isFocused, isSelected }) => {
        return {
        ...styles,
        border: '2px solid white',
        borderRadius: '10px',
        backgroundColor: isFocused ? 'var(--lighter-green)' : 'white',
        color: isFocused ? 'var(--white)' : 'black',
        marginBottom: '2px',
        marginTop: '2px',
        cursor: 'pointer',
        transition: 'background-color 0.15s ease-in-out, color 0.15s ease-in-out'
        };
    },
    menu: base => {
        return {
            ...base,
            backgroundColor: 'rgba(255, 255, 255, 0.15)',
            padding: '8px',
            border: '2px solid var(--green)',
            textAlign: 'center',
            fontFamily: 'var(--coman-font)'
        }
    }
};

function Selection({options, defaultOption, handleChange}) {
    return (
        <Select
            defaultValue={defaultOption}
            options={options}
            className="selection-component"
            onChange={handleChange}
            styles={colourStyles}
            hideSelectedOptions
        />
    )
}

export default Selection;
