import { useCallback } from "react";
import { useForm } from "react-hook-form";

export default function MeContacter() {
	const {
		register,
		handleSubmit,
		formState: { errors },
		reset,
	} = useForm();

	const submit = useCallback(
		(data) => {
			console.log(data);
			reset();
		},
		[reset]
	);

	return (
		<form onSubmit={handleSubmit(submit)} className='formulaire'>
			<h3>Me contacter</h3>

			<div className='corps'>
				<label>Nom</label>
				<input {...register("name", { required: true })} placeholder='Nom' className={errors.name && "invalid"}/>
				{errors.name && errors.name.message}

				<label>Mail</label>
				<input
					{...register("email", {
						pattern: /^[A-Za-z0-9]+@[a-zA-Z0-9.]+\.[a-zA-Z]{2,4}$/i,
						required: "L'e-mail donnée n'est pas au bon format",
					})}
					placeholder='E-mail'
				/>
				{errors.email && errors.email.message}
				
				<label>Message</label>
				<input {...register("message", { required: true })} placeholder='Message' />
				{errors.message && errors.message.message}
			</div>

			<input type='submit' value={"Envoyer"} className="submit" />
		</form>
	);
}
