import { useCallback } from "react";
import { AiFillClockCircle } from "react-icons/ai";
import { VerticalTimeline, VerticalTimelineElement } from "react-vertical-timeline-component";
import "react-vertical-timeline-component/style.min.css";

import photoPro from "../../../images/photoPro.jpg";
import imta from "./imta.svg";

function Formation() {
	const Element = useCallback(({ date, children, iconBackground, iconColor, titre, sousTitre, link, image }) => {
		return (
			<VerticalTimelineElement
				date={date}
				contentStyle={{ backgroundColor: "transparent" }}
				contentArrowStyle={{ display: "none" }}
				iconStyle={{ background: iconBackground, color: iconColor }}>
				{link ? (
					<a href={link} style={{ textDecoration: "none" }}>
						<h3 style={{ marginBottom: 0 }}>{titre}</h3>
					</a>
				) : (
					<h3 style={{ marginBottom: 0 }}>{titre}</h3>
				)}
				<font size='1'>{sousTitre}</font>
				<br />
				<br />
				{image}
				<p>
					École d'ingénieur généraliste
					<br /> Spécialité Développement Logiciel
				</p>
				<br />
			</VerticalTimelineElement>
		);
	}, []);

	return (
		<VerticalTimeline layout='1-column' lineColor='var(--orange-3)' className='timeline'>
			<VerticalTimelineElement
				date="2021 - Ajourd'hui"
				contentStyle={{ backgroundColor: "transparent" }}
				contentArrowStyle={{ display: "none" }}
				iconStyle={{ background: "#00B8DE", color: "#afcb37" }}
				icon={<AiFillClockCircle />}>
				<a href='https://www.imt-atlantique.fr/fr' style={{ textDecoration: "none" }}>
					<h3 style={{ marginBottom: 0 }}>IMT Atlantique</h3>
				</a>
				<font size='1'>(Nantes)</font>
				<br />
				<br />
				<img
					src={imta}
					alt=''
					width='231px'
					height='150px'
					style={{ position: "absolute", top: "calc(50% - 75px)", left: "-265px" }}
				/>
				<p>
					École d'ingénieur généraliste
					<br /> Spécialité Développement Logiciel
				</p>
				<br />
			</VerticalTimelineElement>
		</VerticalTimeline>
	);
}

export default Formation;
